	{l : list α} {b : β l}, f a (a' :: l) (f a' l b) == f a' (a :: l) (f a l b)) → list.rec b f l == list.rec b f l' :=

theorem pow_p_pf_zero {α : Type u} [comm_semiring α] {ps : α} {qs qs' : ℕ}
	(ps_pf : ps = 0) (qs_pf : qs = qs'.succ) :
	ps ^ qs = 0 :=

theorem app_hom_valid_glue {C : Type*} [category_theory.category C] {D : Type*}
	[category_theory.category D] {K : category_theory.grothendieck_topology D}
	{G : C ⥤ D} (H : category_theory.cover_dense K G) [category_theory.full G]
	{ℱ : Dᵒᵖ ⥤ Type v} {ℱ' : category_theory.SheafOfTypes K} (α : G.op ⋙ ℱ ⟶ G.op ⋙ ℱ'.val)
	{X : D} {Y : C} (f : opposite.op X ⟶ opposite.op (G.obj Y)) :
	category_theory.cover_dense.types.app_hom H α X ≫ ℱ'.val.map f = ℱ.map f ≫ α.app (opposite.op Y) :=

theorem deriv_const {𝕜 : Type u} [nondiscrete_normed_field 𝕜] {F : Type v}
	[normed_group F] [normed_space 𝕜 F] (x : 𝕜) (c : F) :
	deriv (λ (x : 𝕜), c) x = 0 :=

theorem forall₂_cons {α β : Type*} {R : α → β → Prop} {a : α} {b : β} {l₁ : list α}
	{l₂ : list β} :
	list.forall₂ R (a :: l₁) (b :: l₂) ↔ R a b ∧ list.forall₂ R l₁ l₂ :=

theorem congr {α E F : Type*} [has_norm E] [has_norm F] {f₁ f₂ : α → E}
	{g₁ g₂ : α → F} {l : filter α} (h : asymptotics.is_o f₁ g₁ l) :
	(∀ (x : α), f₁ x = f₂ x) → (∀ (x : α), g₁ x = g₂ x) →  asymptotics.is_o f₂ g₂ l :=

theorem single_neg {ι : Type u} [dec : decidable_eq ι] {β : ι → Type v}
	[Π (i : ι), add_group (β i)] (i : ι) (x : β i) :
	dfinsupp.single i (-x) = -dfinsupp.single i x :=
